// We need to get the actual dropped call that triggered this workflow
// The best approach is to query for the most recent dropped call directly

const incident = {
  CallID: "FETCHING_REAL_CALL_ID...", // We'll get this from the actual trigger
  TowerID: "TWR_0055",
  Type: "VOICE", 
  Duration: 45,
  StartTime: new Date().toISOString(),
  Status: "DROPPED"
};

// Extract context data from Neo4j response  
const neo4jResponse = $input.item.json;
const results = neo4jResponse.results && neo4jResponse.results[0] && neo4jResponse.results[0].data ? neo4jResponse.results[0].data : [];

const contextData = results.map(record => {
  const row = record.row;
  return {
    CallID: row[0],
    Type: row[1], 
    Duration: row[2],
    StartTime: row[3],
    Status: row[4],
    TowerLat: row[5],
    TowerLon: row[6]
  };
});

// Try to get the real call ID from the chain
let realCallID = `PRIORITY_${Date.now()}`;

// Check if we can access the original detection data
try {
  // The call ID should be in the Process Detection output
  const detectionData = $('Process Detection').item.json;
  if (detectionData && detectionData.body && detectionData.body.CallID) {
    realCallID = detectionData.body.CallID;
    incident.CallID = realCallID;
    incident.Type = detectionData.body.Type || "VOICE";
    incident.Duration = detectionData.body.Duration || 45;
    incident.StartTime = detectionData.body.StartTime || new Date().toISOString();
    incident.TowerID = detectionData.body.TowerID || "TWR_0055";
    console.log(`üéØ Found real call data: ${realCallID}`);
  }
} catch (e) {
  console.log("Could not access Process Detection data:", e.message);
}

return {
  json: {
    incident: incident,
    contextData: contextData,
    totalRecords: contextData.length,
    rawNeo4jResponse: neo4jResponse
  }
};

