// Process Neo4j response to extract dropped call data
const neo4jResponse = $input.item.json;

// Correctly access the data array from the first result set
const dataArray = neo4jResponse.results?.[0]?.data || [];

if (dataArray.length > 0) {
  // If we have data, format each dropped call and return it as a list
  const droppedCalls = dataArray.map(item => {
    // The data for a single call is in the 'row' property
    const row = item.row;

    // Format the data to look like the webhook body
    const droppedCall = {
      body: {
        CallID: row[0],
        Type: row[1],
        Duration: row[2],
        StartTime: row[3],
        Status: row[4],
        TowerID: row[5],
        // Assuming Carrier might be the 7th item if you add it to the query
        // Carrier: row[6] 
      }
    };

    console.log(`‚úÖ Detected dropped call: ${droppedCall.body.CallID}`);
    return { json: droppedCall };
  });

  return droppedCalls;

} else {
  // No dropped calls found - stop workflow
  console.log('üö´ No new dropped calls found');
  return null;
}

